(598, 'xXx: State of the Union', 11679, '', NULL, 3.672572, '/sNnciVEuolaZoIzn5v2rP0pjiL0.jpg', 'en', 'xXx: State of the Union', 'Ice Cube stars as Darius Stone, a thrill-seeking troublemaker whose criminal record and extreme sports obsession make him the perfect candidate to be the newest XXX agent. He must save the U.S. government from a deadly conspiracy led by five-star general and Secretary of Defense George Deckert (played by Willem Dafoe).', '28,12,80,18,9648,53,', '2005-04-27', '2017-07-13 10:55:30', '2017-07-13 10:55:30'),
(599, 'Blood Father', 309886, '', NULL, 3.657344, '/rQ3CKv33u2Z4keC2AYZqi3RGIdX.jpg', 'en', 'Blood Father', 'An ex-con reunites with his estranged wayward 16-year old daughter to protect her from drug dealers who are trying to kill her.', '28,53,', '2016-08-11', '2017-07-13 10:55:34', '2017-07-13 10:55:34'),
(600, 'You Only Live Twice', 667, '', NULL, 3.646699, '/7dnsJZniZK1Hbc9mOFDYa5W0dHi.jpg', 'en', 'You Only Live Twice', 'A mysterious space craft kidnaps a Russian and American space capsule and brings the world on the verge of another World War. James Bond investigates the case in Japan and meets with his archenemy Blofeld. The fifth film from the legendary James Bond series starring Sean Connery as the British super agent.', '28,53,12,', '1967-06-12', '2017-07-13 10:55:38', '2017-07-13 10:55:38'),
(601, 'Resident Evil', 1576, '', NULL, 3.636269, '/fttmSgLEOdBNwQepW2lC6t0mbOm.jpg', 'en', 'Resident Evil', 'When a virus leaks from a top-secret facility, turning all resident researchers into ravenous zombies and their lab animals into mutated hounds from hell, the government sends in an elite military task force to contain the outbreak. Alice and Rain are charged with leading the mission. But they only have three hours before the pathogen becomes airborne and infects the world.', '27,28,878,', '2002-03-15', '2017-07-13 10:55:39', '2017-07-13 10:55:39'),
